#include<string.h>
#include<stddef.h>
#include<stdio.h>

static const char* const input;

int main(void){
	size_t inputLen = strlen(input);
	int floor = 0;
	int basementId = -1;
	
	
	for(size_t i = 0; i < inputLen; ++i){
		if(input[i] == '('){
			floor++;
		} else {
			floor--;
		}
		
		if(floor < 0 && basementId == -1){
			basementId = i+1;
		}
	}
	
	printf("[floor]      : %d\n", floor);
	printf("[basement id]: %d\n", basementId);
}

